import { Splide, SplideSlide } from "@splidejs/react-splide";
import "@splidejs/react-splide/css";

const Carousel = ({
  type,
  things,
  options = {
    perMove: 1,
    type: "loop",
    arrows: true,
    autoplay: true,
    pagination: false,
    gap: "2em",
    perPage: 5,
    breakpoints: {
      768: {
        perPage: 4,
      },
      640: {
        perPage: 2,
      },
    },
    focus: "center",
  },
}) => {
  return (
    <Splide aria-label="infantry-class-slider" dir="ltr" options={options}>
      {Array.isArray(things) &&
        things.map((item, index) => {
          return (
            <SplideSlide key={index} data-splide-interval="2000">
              {type === "company" ? (
                <a href={item.href}>
                  <img
                    src={item.logo}
                    alt="company logo"
                    className="size-full h-32 object-contain"
                  />
                </a>
              ) : type === "review" ? (
                <div className="size-full card">
                  <img
                    src={item.avatar}
                    alt={item.name}
                    className="w-24 h-24 me-8 max-md:hidden"
                  />
                  <div className="flex flex-col justify-between">
                    <div className="text-start">
                      <h3>
                        {item.name}
                        <span className="mx-2 font-thin">/</span>
                        <span className="text-slate-300 text-[10px]">
                          {item.job}
                        </span>
                      </h3>
                      <hr className="my-2 opacity-30" />
                      <p className="text-xs text-justify mt-3">{item.review}</p>
                    </div>
                    <p className="text-slate-200 md:text-[10px] text-[8px] text-end mt-2">
                      آخرین همکاری : {item.lastCooperation}
                    </p>
                  </div>
                </div>
              ) : (
                ""
              )}
            </SplideSlide>
          );
        })}
    </Splide>
  );
};

export default Carousel;
