import { Splide, SplideSlide } from "@splidejs/react-splide";
import "@splidejs/react-splide/css";

const Carousel = ({
  things,

  options = {
    perMove: 1,
    type: "loop",
    arrows: true,
    autoplay: true,
    pagination: false,
    gap: "2em",
    perPage: 5,
    breakpoints: {
      768: {
        perPage: 4,
      },
      640: {
        perPage: 2,
      },
    },
    focus: "center",
  },
}) => {
  return (
    <Splide aria-label="Compaies Slider" options={options}>
      {Array.isArray(things) &&
        things.map((item, index) => {
          return (
            <SplideSlide key={index} data-splide-interval="2000">
              <a href={item.href}>
                <img
                  src={item.logo}
                  alt="company logo"
                  className="size-full h-32 object-contain"
                />
              </a>
            </SplideSlide>
          );
        })}
    </Splide>
  );
};

export default Carousel;
