import React from "react";

const Hero = ({ title, subtitle, light = false }) => {
  return (
    <section
      className={`text-center padding-x pt-36 pb-40 ${
        light ? "bg-slate-50" : "bg-slate-950"
      }`}
      dir="rtl"
    >
      <h1 className={`title-lg ${!light && "text-slate-50"}`}>
        <span className="halfbg-right rounded-full">{title}</span>
      </h1>
      <hr className="mx-auto my-6 border-slate-300 w-2/5" />
      <h3
        className={`font-thin ${light ? "text-slate-700" : "text-slate-300"}`}
      >
        {subtitle}
      </h3>
    </section>
  );
};

export default Hero;
