import { categories, sortby } from "../../Constants";
import { blogs } from "../../data/Blogs";
import { Blog_Card } from "../Portal";

const Blog_Overview = () => {
  return (
    <section className="container padding" dir="rtl">
      <div className="flex-seperate mb-5 max-md:flex-col border-b border-blue-200 border-opacity-30 pb-3">
        <div className="flex gap-x-1">
          <label htmlFor="category" className="label">
            دسته بندی :{" "}
          </label>
          <select id="category" className="select">
            {categories.map((item, index) => {
              return <option key={index}>{item}</option>;
            })}
          </select>
        </div>
        <form className="flex items-center gap-1">
          {sortby.map((item, index) => {
            return (
              <>
                <span className="cursor-pointer">{item}</span>
                {index + 1 < sortby.length && (
                  <span className="text-slate-300">|</span>
                )}
              </>
            );
          })}
          <fieldset>
            <legend>مرتب سازی : </legend>

            <input
              id="draft"
              class="peer/draft"
              type="radio"
              name="status"
              checked
            />
            <label for="draft" class="peer-checked/draft:text-sky-500">
              Draft
            </label>

            <input
              id="published"
              class="peer/published"
              type="radio"
              name="status"
            />
            <label for="published" class="peer-checked/published:text-sky-500">
              Published
            </label>
          </fieldset>
        </form>
      </div>
      <div className="grid lg:grid-cols-3 md:grid-cols-2 grid-cols-1 gap-5">
        {blogs.map((item, index) => {
          return <Blog_Card blog={item} key={index} />;
        })}
      </div>
    </section>
  );
};

export default Blog_Overview;
