import { Link } from "react-router-dom";

const Overall = ({ thing, light = false }) => {
  return (
    <section
      className={`min-h-[50vh] max-h-screen padding-x py-52 text-center ${
        light ? "bg-slate-50" : "bg-slate-950"
      }`}
    >
      <h2 className={`title-super ${!light && "text-slate-50"}`}>
        {thing.title}
      </h2>
      <p className={light ? "text-slate-700" : "text-slate-300"}>
        {thing.describe}
      </p>
      <div className="inline-flex gap-8 mx-auto">
        {thing.links.map((item, index) => {
          return (
            <Link
              to={item.link}
              className={`button ${
                light && index > 0
                  ? "button-dark"
                  : light && index === 0
                  ? "button-light"
                  : index > 0
                  ? "button-light"
                  : "button-dark"
              }`}
            >
              {item.label}
            </Link>
          );
        })}
      </div>
    </section>
  );
};

export default Overall;
