import { Link } from "react-router-dom";

const Overall = ({ thing, light = false }) => {
  return (
    <section
      className={`min-h-[50vh] max-h-screen padding-x pb-96 text-center ${
        light ? "bg-slate-50" : "bg-slate-950"
      }`}
    >
      <div className="">
        <h2 className={!light && "text-slate-50"}>{thing.title}</h2>
        <p className={light ? "text-slate-700" : "text-slate-300"}>
          {thing.describe}
        </p>
        <div className="flex gap-8">
          {thing.links.map((item, index) => {
            return (
              <Link
                to={item.link}
                className={`button ${
                  light && index > 0
                    ? "button-dark"
                    : light && index === 0
                    ? "button-light"
                    : index > 0
                    ? "button-light"
                    : "button-dark"
                }`}
              >
                {item.label}
              </Link>
            );
          })}
        </div>
      </div>
    </section>
  );
};

export default Overall;
