import { useParams } from "react-router-dom";
import { Hero } from "../comps/Portal";
import { services } from "../Constants";

const Single_Service = () => {
  const { service } = useParams();
  const theService = services.find(
    (e) =>
      e &&
      e.title.replace(/\//g, "%2F").replace(/\s+/g, "-").toLowerCase() ===
        service.replace(/\//g, "%2F").replace(/\s+/g, "-").toLowerCase()
  );

  return (
    <>
      <Hero title={theService.title} background={theService.cover} />
      <section className="grid md:grid-cols-4 gap-8 container padding"></section>
    </>
  );
};

export default Single_Service;
