import { customerReviews } from "../../data/Constants";
import { Carousel } from "../Portal";
const Customers = () => {
  return (
    <section className="padding-x py-10 text-center">
      <h2 className="title-lg mac-bg">چیزی که دیگران دربارمون میگن</h2>
      <div dir="ltr" className="mt-10">
        <Splide
          aria-label="Customers Slider"
          options={{
            perMove: 1,
            type: "loop",
            arrows: true,
            autoplay: true,
            pagination: false,
            gap: "2em",
            perPage: 3,
            breakpoints: {
              1200: {
                perPage: 1,
              },
            },
            focus: "center",
          }}
        >
          {customerReviews.map((item, index) => {
            return <SplideSlide key={index}></SplideSlide>;
          })}
        </Splide>
        <Carousel
          things={customerReviews}
          options={{
            perMove: 1,
            type: "loop",
            arrows: true,
            autoplay: true,
            pagination: false,
            gap: "2em",
            perPage: 3,
            breakpoints: {
              1200: {
                perPage: 1,
              },
            },
            focus: "center",
          }}
        />
      </div>
    </section>
  );
};

export default Customers;
