const Blog_Card = ({ blog }) => {
  return (
    <div className="overflow-hidden rounded-2xl border-4 border-blue-400 group">
      <img
        src={blog.cover}
        alt={blog.title}
        className="h-[200px] w-full bg-fullobject"
      />
      <div className="px-3 py-1">
        <h3 className="title group-hover:scale-50">{blog.title}</h3>
        <p className="line-clamp-2 para text-ellipsis group-hover:scale-100">
          {blog.paragraphes[0].paragraphe}
        </p>
        <p className="text-end label">{blog.uploadData}</p>
      </div>
    </div>
  );
};

export default Blog_Card;
