import { useEffect, useState } from "react";
import { categories, sortby } from "../../Constants";
import { blogs } from "../../data/Blogs";
import { Blog_Card } from "../Portal";

const Blog_Overview = () => {
  const [category, setCategory] = useState("همه");
  const handleCategoryChange = () => {
    let selectedCategory = document.getElementById("category").value;
    setCategory(selectedCategory);
  };

  const [serchValue, setSerchValue] = useState(null);
  const handleSearch = () => {
    let searchedThing = document.getElementById("search").value;
    setSerchValue(searchedThing);
  };

  const [sort, setSort] = useState(0);
  const handleSort = (index) => {
    setSort(index);
  };

  const SortedBlogs = blogs;

  if (sort == 1) {
    SortedBlogs = blogs.reverse();
  } else if (sort == 2) {
    SortedBlogs = blogs.sort((a, b) => a.suggested - b.suggested);
  }

  const [page, setPage] = useState(1);
  const isFirstPage = page === 1;
  const isLastPage = page === SortedBlogs.length / 12;

  const theBlogs = [];
  for (let i = (page - 1) * 12; i < page * 12; i++) {
    if (SortedBlogs[i]) {
      theBlogs.push(SortedBlogs[i]);
    }
  }

  const pageDivisons = [];
  for (let i = 1; i < theBlogs.length / 12 + 1; i++) {
    pageDivisons.push(i);
  }

  const handlePageChanging = (e) => {
    const pageNumber = parseInt(e.target.value);
    setPage(pageNumber);
  };

  const handleNextPage = () => {
    if (!isLastPage) {
      setPage(page + 1);
    }
  };

  const handlePrevPage = () => {
    if (!isFirstPage) {
      setPage(page - 1);
    }
  };

  useEffect(() => {
    const selectBox = document.getElementById("pageSelect");
    if (selectBox) {
      selectBox.value = page;
    }
  }, [page]);

  return (
    <section className="container padding" dir="rtl">
      <div className="flex-seperate mb-5 max-md:flex-col gap-5 border-b border-blue-200 border-opacity-30 pb-3">
        <div className="flex gap-x-1">
          <label htmlFor="category" className="label">
            دسته بندی :{" "}
          </label>
          <select
            id="category"
            className="select w-[180px]"
            onChange={handleCategoryChange}
          >
            {categories.map((item, index) => {
              return (
                <option key={index} value={item}>
                  {item}
                </option>
              );
            })}
          </select>
        </div>
        <div className="flex gap-x-3 border-mac rounded bg-blue-400 text-blue-50">
          <button onClick={handleSearch}>
            <i className="fas fa-magnifying-glass"></i>
          </button>
          <input
            type="text"
            id="search"
            className="field allunset bg-transparent"
          />
        </div>
        <div className="flex items-center gap-1 text-slate-400 font-thin select-none">
          {sortby.map((item, index) => {
            return (
              <>
                <span
                  className={`cursor-pointer ${
                    index === sort && "text-blue-600"
                  }`}
                  onClick={() => handleSort(index)}
                >
                  {item}
                </span>
                {index + 1 < sortby.length && (
                  <span className="opacity-50">/</span>
                )}
              </>
            );
          })}
        </div>
      </div>
      <div className="grid lg:grid-cols-3 md:grid-cols-2 grid-cols-1 gap-5">
        {serchValue
          ? theBlogs
              .filter(
                (item) =>
                  item.title && item.title.toString().includes(serchValue)
              )
              .map((item, index) => {
                return <Blog_Card blog={item} key={index} />;
              })
          : theBlogs
              .filter(
                (item) => category === "همه" || item.category === category
              )
              .map((item, index) => {
                return <Blog_Card blog={item} key={index} />;
              })}
      </div>
      <div className="max-w-[250px] mx-auto text-center mt-12">
        <button
          onClick={handlePrevPage}
          className={isFirstPage && "opacity-70"}
        >
          Prev
        </button>
        <select
          id="pageSelect"
          className="mx-1 bg-transparent py-0 px-[30px!important] border border-transparent shadow-none focus:border focus:border-slate-100"
          onChange={handlePageChanging}
        >
          {pageDivisons.map((item, index) => {
            return (
              <option key={index} value={item} className="text-slate-950">
                {item} of {theBlogs.length / 9}
              </option>
            );
          })}
        </select>

        <button onClick={handleNextPage} className={isLastPage && "opacity-70"}>
          Next
        </button>
      </div>
    </section>
  );
};

export default Blog_Overview;
