import React from "react";

const Hero = ({ title, subtitle }) => {
  return (
    <section
      className="text-center pt-40 pb-48 bg-indigo-600 text-slate-50"
      dir="rtl"
    >
      <h1 className="title-lg">{title}</h1>
      <h3>{subtitle}</h3>
    </section>
  );
};

export default Hero;
