const Tip = ({ message }) => {
  return (
    <div className="bg-indigo-400 inline-flex items-center rounded-full text-slate-50 p-3">
      <span className="bg-indigo-500 rounded-full text-indigo-900 font-extrabold px-5 text-center">
        بخون
      </span>
      <p dangerouslySetInnerHTML={{ __html: message }} className="mx-5" />
      <i className="fal fa-chevron-left" />
    </div>
  );
};

export default Tip;
