import { Link } from "react-router-dom";
import { blogs } from "../../data/Blogs";

const Blog_Side = () => {
  const latestBlogs = blogs.slice(-3).reverse();

  return (
    <aside className="grid gap-y-5">
      <div className="border-mac py-2">
        <h4># آخرین مطالب</h4>
        <div className="grid gap-y-5 mt-5">
          {latestBlogs.map((item, index) => {
            return (
              <Link to={"/blog/" + item.title} className="flex" key={index}>
                <img
                  src={item.cover}
                  alt={item.title}
                  className="size-20 rounded-lg"
                />
                <div>
                  <h3>{item.title}</h3>
                  <span className="warn text-xs">{item.category}</span>
                </div>
              </Link>
            );
          })}
        </div>
      </div>
    </aside>
  );
};

export default Blog_Side;
