const Blog_Card = ({ blog }) => {
  return (
    <div className="overflow-hidden rounded-2xl border-4">
      <img
        src={blog.cover}
        alt={blog.title}
        className="h-[200px] w-full bg-fullobject"
      />
      <h3 className="title">{blog.title}</h3>
      <p>{blog.preview}</p>
    </div>
  );
};

export default Blog_Card;
